// ==UserScript==
// @name			Quick Image Downloader
// @namespace		https://openuserjs.org/users/mikhailsdv
// @version			0.1
// @description		Shows download button on every image on the page automatically
// @author			Misha Saidov, Rene Andre Bedonia Jocsing
// @license			MIT
// @match			https://*/*
// @match			http://*/*
// @icon			https://i.ibb.co/XxcFQVC/logo.png
// @author			Misha Saidov, Rene Andre Bedonia Jocsing
// @grant			none
// ==/UserScript==

(function() {
	"use strict";

	window.focus();

	// Periodically add download buttons every 5 seconds
	setInterval(() => {
		appendDownloadButtons();
	}, 5000);

	async function appendDownloadButtons() {
		Array.from(document.querySelectorAll("*:not(img)")).forEach(element => {
			if (element.offsetParent) {
				let computedStyle = getComputedStyle(element);
				let match = computedStyle.backgroundImage.match(/^url\(['"](.+?)['"]\)/);

				if (match && match[1]) {
					// Check if the button already exists
					if (!element.querySelector("[data-download-image-button]")) {
						appendButton({
							top: element.offsetTop,
							left: element.offsetLeft,
							width: element.offsetWidth,
							height: element.offsetHeight,
							element: element.offsetParent,
							url: match[1],
							fileName: getFileName(match[1])
						});
					}
				}
			}
		});

		Array.from(document.getElementsByTagName("img")).forEach(element => {
			if (element.offsetParent) {
				// Check if the button already exists
				if (!element.querySelector("[data-download-image-button]")) {
					appendButton({
						top: element.offsetTop,
						left: element.offsetLeft,
						width: element.offsetWidth,
						height: element.offsetHeight,
						element: element.offsetParent,
						url: element.src,
						fileName: getFileName(element.src)
					});
				}
			}
		});
	}

	const appendButton = ({
		width,
		height,
		top,
		left,
		element,
		url,
		fileName
	}) => {
		let button = document.createElement("button");
		let size = 20;
		let styles;
		if (!document.querySelector("style[data-download-image-button-styles]")) {
			styles = document.createElement("style");
			styles.setAttribute("data-download-image-button-styles", "");
			styles.innerHTML = `
				[data-download-image-button] {
					z-index: 99999;
					position: absolute;
					border: none;
					outline: none;
					padding: 0;
					margin: 0;
					cursor: pointer;
					background-color: white;
					opacity: 0.5;
					background-size: 60%;
					background-position: center;
					background-repeat: no-repeat;
					background-image: url("data:image/svg+xml;base64,");
					border-radius: 100%;
					height: ${size}px;
					min-width: ${size}px;
					min-height: ${size}px;
					width: ${size}px;
					box-shadow: 0 0 0 rgba(0, 0, 0, 0.3);
					transition: all .2s ease-out;
				}
				[data-download-image-button]:hover {
					transform: scale(1.2);
					opacity: 1;
					box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
				}
				[data-download-image-button]:active {
					transform: scale(1.1);
					opacity: 1;
					box-shadow: 0 0 6px rgba(0, 0, 0, 0.3);
				}
			`;
			document.head.appendChild(styles);
		}

		button.style.top = top + (height / 2) - (size / 2) + "px";
		button.style.left = left + (width / 2) - (size / 2) + "px";
		button.setAttribute("data-download-image-button", "");
		button.addEventListener("click", e => {
			e.preventDefault();
			e.stopImmediatePropagation();
			e.stopPropagation();
			downloadFile(fileName, url);
		});
		element.prepend(button);
	}

	const toDataURL = url => {
		return fetch(url).then(response => {
			return response.blob();
		}).then(blob => {
			return URL.createObjectURL(blob);
		});
	}

	async function downloadFile(fileName, src) {
		const a = document.createElement("a");
		a.href = await toDataURL(src);
		a.download = fileName;
		document.body.appendChild(a);
		a.click();
		a.remove();
	}

	const getFileName = url => {
		if (/^data:image\/([a-zA-Z0-9]+)/.test(url)) {
			let extension = url.match(/^data:image\/([a-zA-Z0-9]+)/);
			return "image." + extension[1];
		}
		else {
			let match = url.match(/^.*\/(.+?)(\?.+?)?$/);
			if (match && match[1]) {
				return match[1];
			}
			else {
				return "image.jpg";
			}
		}
	}

})();